using System;\nusing System.Runtime.Caching;\n\npublic class CacheMonitor\n{\n    public static void Main()\n    {\n        ObjectCache cache = MemoryCache.Default;\n        long totalSize = 0;\n\n        foreach (var item in cache)\n        {\n            totalSize += ((System.Runtime.Caching.MemoryCacheEntry)item.Value).Size;\n        }\n\n        Console.WriteLine("Total Cache Size: " + totalSize + " bytes");\n    }\n}