using System;\nusing System.Diagnostics;\n\npublic class Program\n{\n    public static void Main()\n    {\n        var cacheMemoryCounter = new PerformanceCounter("ASP.NET Applications", "Cache % Memory Limit Used", "__Total__");\n        var cacheTotalMemoryCounter = new PerformanceCounter("ASP.NET Applications", "Cache Total Memory Used", "__Total__");\n\n        Console.WriteLine("Cache % Memory Limit Used: " + cacheMemoryCounter.NextValue() + "%");\n        Console.WriteLine("Cache Total Memory Used: " + cacheTotalMemoryCounter.NextValue() + " bytes");\n    }\n}