using mshtml;\nusing System;\nusing System.Windows.Forms;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create a new instance of the WebBrowser control\n        WebBrowser ie = new WebBrowser();\n        \n        // Navigate to the desired website\n        ie.Navigate("http://website.com");\n\n        // Wait for the document to load\n        ie.DocumentCompleted += new WebBrowserDocumentCompletedEventHandler(DocumentCompleted);\n        \n        // Keep the application running\n        Application.Run();\n    }\n\n    private static void DocumentCompleted(object sender, WebBrowserDocumentCompletedEventArgs e)\n    {\n        WebBrowser ie = sender as WebBrowser;\n        HTMLDocument theDoc = (HTMLDocument)ie.Document.DomDocument;\n        \n        // Get the parent DIV element by its ID\n        HTMLDivElement myDiv = (HTMLDivElement)theDoc.getElementById("divID");\n\n        if (myDiv != null)\n        {\n            // Iterate through the child elements of the parent DIV\n            foreach (IHTMLElement child in myDiv.children)\n            {\n                if (child is HTMLDivElement)\n                {\n                    HTMLDivElement nestedDiv = (HTMLDivElement)child;\n                    // Process the nested DIV element as needed\n                    Console.WriteLine(nestedDiv.innerText);\n                }\n            }\n        }\n        else\n        {\n            Console.WriteLine("DIV with the specified ID not found.");\n        }\n    }\n}