using System;\nusing Microsoft.WindowsAPICodePack.Shell;\nusing Microsoft.WindowsAPICodePack.Shell.PropertySystem;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string imagePath = @"path\to\your\image.jpg";\n        GetImageProperties(imagePath);\n    }\n\n    static void GetImageProperties(string imagePath)\n    {\n        using (ShellFile shellFile = ShellFile.FromFilePath(imagePath))\n        {\n            var width = shellFile.Properties.System.Image.HorizontalSize.Value;\n            var height = shellFile.Properties.System.Image.VerticalSize.Value;\n\n            Console.WriteLine($"Width: {width}");\n            Console.WriteLine($"Height: {height}");\n        }\n    }\n}