using System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        // Array example\n        int[] array = new int[] { 1, 2, 3 };\n        // You cannot change the size of the array\n        // array[3] = 4; // This would throw an IndexOutOfRangeException\n        array[0] = 10; // You can change the value of an element\n        Console.WriteLine("Array: " + string.Join(", ", array));\n\n        // IList example\n        IList<int> list = new List<int> { 1, 2, 3 };\n        list.Add(4); // You can add new elements\n        list.Remove(2); // You can remove elements\n        Console.WriteLine("IList: " + string.Join(", ", list));\n    }\n}