using DocumentFormat.OpenXml;\nusing DocumentFormat.OpenXml.Packaging;\nusing DocumentFormat.OpenXml.Spreadsheet;\n\nnamespace OpenXmlExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // Create a spreadsheet document by supplying the filepath.\n            // By default, AutoSave = true, Editable = true, and Type = xlsx.\n            using (SpreadsheetDocument spreadsheetDocument = SpreadsheetDocument.Create("example.xlsx", SpreadsheetDocumentType.Workbook))\n            {\n                // Add a WorkbookPart to the document.\n                WorkbookPart workbookPart = spreadsheetDocument.AddWorkbookPart();\n                workbookPart.Workbook = new Workbook();\n\n                // Add a WorksheetPart to the WorkbookPart.\n                WorksheetPart worksheetPart = workbookPart.AddNewPart<WorksheetPart>();\n                worksheetPart.Worksheet = new Worksheet(new SheetData());\n\n                // Add Sheets to the Workbook.\n                Sheets sheets = spreadsheetDocument.WorkbookPart.Workbook.AppendChild(new Sheets());\n\n                // Append a new sheet and associate it with the workbook.\n                Sheet sheet = new Sheet()\n                {\n                    Id = spreadsheetDocument.WorkbookPart.GetIdOfPart(worksheetPart),\n                    SheetId = 1,\n                    Name = "Sheet1"\n                };\n                sheets.Append(sheet);\n\n                // Add some data to the worksheet.\n                SheetData sheetData = worksheetPart.Worksheet.GetFirstChild<SheetData>();\n                Row row = new Row() { RowIndex = 1 };\n                Cell cell = new Cell() { CellReference = "A1", CellValue = new CellValue("Hello, OpenXML!"), DataType = CellValues.String };\n                row.Append(cell);\n                sheetData.Append(row);\n\n                // Save the Workbook.\n                workbookPart.Workbook.Save();\n            }\n        }\n    }\n}