using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main()\n    {\n        string JIRA_URL = "http://your-jira-instance/rpc/soap/jirasoapservice-v2";\n        string username = "your_username";\n        string password = "your_password";\n\n        // Create the SOAP client\n        JIRAService.JiraSoapServiceService client = new JIRAService.JiraSoapServiceService();\n        client.Url = JIRA_URL;\n\n        // Login to JIRA\n        string token = client.login(username, password);\n\n        try\n        {\n            // Link issues\n            string issueKey1 = "PROJECT-123";\n            string issueKey2 = "PROJECT-456";\n            string linkType = "relates to";\n\n            client.linkIssues(token, issueKey1, issueKey2, linkType);\n\n            Console.WriteLine("Issues linked successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error linking issues: " + ex.Message);\n        }\n        finally\n        {\n            // Logout\n            client.logout(token);\n        }\n    }\n}