using System.ComponentModel.DataAnnotations;\n\npublic class DomainValidationAttribute : ValidationAttribute\n{\n    private readonly string _allowedDomain;\n\n    public DomainValidationAttribute(string allowedDomain)\n    {\n        _allowedDomain = allowedDomain;\n    }\n\n    protected override ValidationResult IsValid(object value, ValidationContext validationContext)\n    {\n        if (value != null)\n        {\n            string[] emailParts = value.ToString().Split('@');\n            if (emailParts.Length == 2 && emailParts[1].ToLower() == _allowedDomain.ToLower())\n            {\n                return ValidationResult.Success;\n            }\n            else\n            {\n                return new ValidationResult($"Email domain must be {_allowedDomain}");\n            }\n        }\n        return new ValidationResult("Invalid email address");\n    }\n}\n\npublic class LoginViewModel\n{\n    [Required(ErrorMessage = "Email address is required")]\n    [EmailAddress(ErrorMessage = "Invalid email address")]\n    [DomainValidation("example.com", ErrorMessage = "Email domain must be example.com")]\n    public string EmailAddress { get; set; }\n}