using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        dynamic objAccess = Activator.CreateInstance(Type.GetTypeFromProgID("Access.Application"));\n        try\n        {\n            objAccess.OpenCurrentDatabase("C:\\MYDB.mdb", true); // Open in exclusive mode\n\n            // Call the VBA function to disable the startup form\n            objAccess.Run("DisableStartupForm");\n\n            // Perform the XML export\n            objAccess.ExportXML(0, "TestTable", "c:\\test.xml");\n        }\n        finally\n        {\n            // Close the database and quit Access\n            objAccess.CloseCurrentDatabase();\n            objAccess.Quit();\n            Marshal.ReleaseComObject(objAccess);\n        }\n    }\n}