using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic class UserHobby\n{\n    public string User { get; set; }\n    public string Hobby { get; set; }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<UserHobby> userHobbies = new List<UserHobby>\n        {\n            new UserHobby { User = "Bob", Hobby = "Football" },\n            new UserHobby { User = "Bob", Hobby = "Golf" },\n            new UserHobby { User = "Bob", Hobby = "Tennis" },\n            new UserHobby { User = "Sue", Hobby = "Sleeping" },\n            new UserHobby { User = "Sue", Hobby = "Drinking" }\n        };\n\n        var result = userHobbies\n            .GroupBy(uh => uh.User)\n            .Select(g => new \n            {\n                User = g.Key,\n                Hobby = string.Join(", ", g.Select(uh => uh.Hobby))\n            })\n            .ToList();\n\n        foreach (var item in result)\n        {\n            Console.WriteLine($"User: {item.User}, Hobby: {item.Hobby}");\n        }\n    }\n}