using System;\nusing System.Data;\nusing Oracle.ManagedDataAccess.Client;\n\npublic class OracleXmlInsertExample\n{\n    public void InsertRecord()\n    {\n        string connectionString = "User Id=<username>;Password=<password>;Data Source=<datasource>";\n\n        using (OracleConnection connection = new OracleConnection(connectionString))\n        {\n            connection.Open();\n\n            string sql = @"\n                INSERT INTO YourTable (ID, UPDATE_DATE, OtherColumn)\n                VALUES (MySeq.nextval, SYSDATE, :OtherValue)";\n\n            using (OracleCommand command = new OracleCommand(sql, connection))\n            {\n                command.Parameters.Add(new OracleParameter("OtherValue", OracleDbType.Varchar2)).Value = "YourValue";\n\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}