public class CookieEndpointBehavior : IEndpointBehavior\n   {\n       private string _cookieName;\n       private string _cookieValue;\n\n       public CookieEndpointBehavior(string cookieName, string cookieValue)\n       {\n           _cookieName = cookieName;\n           _cookieValue = cookieValue;\n       }\n\n       public void AddBindingParameters(ServiceEndpoint endpoint, BindingParameterCollection bindingParameters)\n       {\n           // Not needed for this example\n       }\n\n       public void ApplyClientBehavior(ServiceEndpoint endpoint, ClientRuntime clientRuntime)\n       {\n           clientRuntime.MessageInspectors.Add(new CookieMessageInspector(_cookieName, _cookieValue));\n       }\n\n       public void ApplyDispatchBehavior(ServiceEndpoint endpoint, EndpointDispatcher endpointDispatcher)\n       {\n           // Not needed for this example\n       }\n\n       public void Validate(ServiceEndpoint endpoint)\n       {\n           // Not needed for this example\n       }\n   }