using System;\nusing System.IO;\n\npublic class FileComparer\n{\n    public static bool AreFilesEqual(string filePath1, string filePath2)\n    {\n        using (var fs1 = new FileStream(filePath1, FileMode.Open, FileAccess.Read))\n        using (var fs2 = new FileStream(filePath2, FileMode.Open, FileAccess.Read))\n        {\n            if (fs1.Length != fs2.Length)\n                return false;\n            \n            int byte1, byte2;\n            do\n            {\n                byte1 = fs1.ReadByte();\n                byte2 = fs2.ReadByte();\n            } while (byte1 == byte2 && byte1 != -1);\n\n            return byte1 == byte2;\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string file1 = "path/to/first/file";\n        string file2 = "path/to/second/file";\n\n        bool areEqual = FileComparer.AreFilesEqual(file1, file2);\n        Console.WriteLine($"Files are {(areEqual ? "equal" : "not equal")}");\n    }\n}