using System;\nusing System.Net;\nusing System.Net.Mail;\n\nnamespace SmtpExample\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            // SMTP server details\n            string smtpServer = "smtp.yourserver.com";\n            int smtpPort = 587; // Commonly used SMTP port\n            string smtpUser = "yourusername";\n            string smtpPass = "yourpassword";\n\n            // Creating the SmtpClient object\n            SmtpClient client = new SmtpClient(smtpServer, smtpPort)\n            {\n                Credentials = new NetworkCredential(smtpUser, smtpPass),\n                EnableSsl = true // Enable SSL for secure communication\n            };\n\n            // Creating the MailMessage object\n            MailMessage mailMessage = new MailMessage\n            {\n                From = new MailAddress("from@example.com"),\n                Subject = "Test Mail",\n                Body = "This is a test email.",\n                IsBodyHtml = false\n            };\n\n            // Add recipient\n            mailMessage.To.Add("to@example.com");\n\n            try\n            {\n                // Send the email\n                client.Send(mailMessage);\n                Console.WriteLine("Email sent successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error sending email: " + ex.Message);\n            }\n        }\n    }\n}