using System;\nusing System.Diagnostics;\nusing System.Threading;\nusing System.Windows.Forms;\n\npublic class MyForm : Form\n{\n    private RichTextBox outputBox;\n\n    public MyForm()\n    {\n        outputBox = new RichTextBox { Dock = DockStyle.Fill };\n        this.Controls.Add(outputBox);\n\n        // Example button to start the process\n        var startButton = new Button { Text = "Start Process" };\n        startButton.Click += StartButton_Click;\n        this.Controls.Add(startButton);\n    }\n\n    private void StartButton_Click(object sender, EventArgs e)\n    {\n        Thread processThread = new Thread(RunProcess);\n        processThread.Start();\n    }\n\n    private void RunProcess()\n    {\n        Process process = new Process();\n        process.StartInfo.FileName = "xdiscbld.exe";\n        process.StartInfo.Arguments = "your-arguments-here";\n        process.StartInfo.RedirectStandardOutput = true;\n        process.StartInfo.RedirectStandardError = true;\n        process.StartInfo.UseShellExecute = false;\n        process.StartInfo.CreateNoWindow = true;\n        process.OutputDataReceived += (sender, args) => AppendOutput(args.Data);\n        process.ErrorDataReceived += (sender, args) => AppendOutput(args.Data);\n        \n        process.Start();\n        process.BeginOutputReadLine();\n        process.BeginErrorReadLine();\n        process.WaitForExit();\n    }\n\n    private void AppendOutput(string text)\n    {\n        if (outputBox.InvokeRequired)\n        {\n            outputBox.Invoke(new Action<string>(AppendOutput), text);\n        }\n        else\n        {\n            outputBox.AppendText(text + Environment.NewLine);\n        }\n    }\n}