using System.Drawing;\nusing System.Windows.Forms;\n\npublic class FontSizeExample : Form\n{\n    public FontSizeExample()\n    {\n        Label label = new Label();\n        label.Text = "Hello, World!";\n        label.Location = new Point(10, 10);\n\n        // Original font\n        Font originalFont = label.Font;\n\n        // Create a new font with the desired size, keeping other properties the same\n        float newSize = 16.0f;\n        Font newFont = new Font(originalFont.FontFamily, newSize, originalFont.Style);\n\n        // Set the new font to the label\n        label.Font = newFont;\n\n        this.Controls.Add(label);\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.Run(new FontSizeExample());\n    }\n}