using System;\nusing System.DirectoryServices;\n\npublic class LdapHelper\n{\n    private string _path;\n    private string _username;\n    private string _password;\n\n    public LdapHelper(string path, string username, string password)\n    {\n        _path = path;\n        _username = username;\n        _password = password;\n    }\n\n    public bool ValidateUser(string domain, string username, string password)\n    {\n        string domainAndUsername = $"{domain}\\{username}";\n        DirectoryEntry entry = new DirectoryEntry(_path, domainAndUsername, password);\n\n        try\n        {\n            // Bind to the native AdsObject to force authentication.\n            object obj = entry.NativeObject;\n            return true;\n        }\n        catch (DirectoryServicesCOMException)\n        {\n            return false;\n        }\n    }\n}\n\n// Unit test example\npublic class LdapHelperTests\n{\n    [Fact]\n    public void ValidateUser_ShouldReturnTrueForValidUser()\n    {\n        // Arrange\n        var ldapHelper = new LdapHelper("LDAP://localhost:389", "testAdmin", "adminPassword");\n\n        // Act\n        var result = ldapHelper.ValidateUser("testDomain", "testUser", "testPassword");\n\n        // Assert\n        Assert.True(result);\n    }\n}