// Create a FormsAuthenticationTicket with a specific version\nFormsAuthenticationTicket ticket = new FormsAuthenticationTicket(\n    version: 1, // Version number\n    name: "username",\n    issueDate: DateTime.Now,\n    expiration: DateTime.Now.AddMinutes(30),\n    isPersistent: false,\n    userData: "additional data"\n);\n\n// Access the Version property\nint ticketVersion = ticket.Version;\nConsole.WriteLine($"Ticket Version: {ticketVersion}");