using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string xmlData = "<Rows><Row><Id>1</Id><Name>John Doe</Name><Age>30</Age></Row><Row><Id>2</Id><Name>Jane Doe</Name><Age>25</Age></Row></Rows>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xmlData);\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            conn.Open();\n\n            using (SqlCommand cmd = new SqlCommand("InsertXMLData", conn))\n            {\n                cmd.CommandType = CommandType.StoredProcedure;\n\n                SqlParameter param = new SqlParameter();\n                param.ParameterName = "@xmlData";\n                param.Value = xmlData;\n                param.SqlDbType = SqlDbType.Xml;\n\n                cmd.Parameters.Add(param);\n\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}