using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            // Declare new process and name it p1\n            Process p1 = Process.Start("iexplore", "http://www.google.com");\n            \n            // Set up a timer to kill the process after 2 minutes (120000 milliseconds)\n            Timer timer = new Timer(KillProcess, p1, 120000, Timeout.Infinite);\n            \n            // Wait for user input to exit\n            Console.ReadLine();\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Problem with Process: {0}", ex.Message);\n        }\n    }\n\n    static void KillProcess(object processObj)\n    {\n        Process process = processObj as Process;\n        if (process != null && !process.HasExited)\n        {\n            process.Kill();\n            Console.WriteLine("Process killed after 2 minutes.");\n        }\n    }\n}