using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    private const string tailExecutable = @"C:\tail.exe";\n    private const string logFile = @"C:\test.log";\n\n    private static void ReadStdOut()\n    {\n        var psi = new ProcessStartInfo\n        {\n            FileName = tailExecutable,\n            Arguments = String.Format("-f \"{0}\"", logFile),\n            UseShellExecute = false,\n            RedirectStandardOutput = true,\n            RedirectStandardError = true,\n            CreateNoWindow = true\n        };\n\n        var tail = new Process();\n        tail.StartInfo = psi;\n        tail.OutputDataReceived += Tail_OutputDataReceived;\n        tail.ErrorDataReceived += Tail_ErrorDataReceived;\n\n        tail.Start();\n        tail.BeginOutputReadLine();\n        tail.BeginErrorReadLine();\n        tail.WaitForExit();\n    }\n\n    static void Tail_OutputDataReceived(object sender, DataReceivedEventArgs e)\n    {\n        if (!string.IsNullOrEmpty(e.Data))\n        {\n            Console.WriteLine(e.Data);\n        }\n    }\n\n    static void Tail_ErrorDataReceived(object sender, DataReceivedEventArgs e)\n    {\n        if (!string.IsNullOrEmpty(e.Data))\n        {\n            Console.Error.WriteLine(e.Data);\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        ReadStdOut();\n    }\n}