public Color x, y;\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    CreateColorPanels(Color.Red, Color.Salmon, 10); // Example with 10 panels\n}\n\nvoid CreateColorPanels(Color startColor, Color endColor, int numberOfPanels)\n{\n    for (int i = 0; i < numberOfPanels; i++)\n    {\n        float ratio = (float)i / (numberOfPanels - 1);\n        int r = (int)(startColor.R * (1 - ratio) + endColor.R * ratio);\n        int g = (int)(startColor.G * (1 - ratio) + endColor.G * ratio);\n        int b = (int)(startColor.B * (1 - ratio) + endColor.B * ratio);\n\n        Color intermediateColor = Color.FromArgb(r, g, b);\n        AddColorPanel(intermediateColor);\n    }\n}\n\nvoid AddColorPanel(Color color)\n{\n    Panel panel = new Panel\n    {\n        Width = 150,\n        Height = 50,\n        BackColor = color\n    };\n    \n    this.Controls.Add(panel);\n}