using System;\nusing System.Reflection;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Example class with an event\n        var exampleType = typeof(ExampleClass);\n        var eventInfo = exampleType.GetEvent("EventName");\n\n        if (eventInfo != null)\n        {\n            var eventHandlerType = eventInfo.EventHandlerType;\n            var invokeMethod = eventHandlerType.GetMethod("Invoke");\n            var parameters = invokeMethod.GetParameters();\n\n            foreach (var parameter in parameters)\n            {\n                Console.WriteLine($"{parameter.Name}: {parameter.ParameterType}");\n            }\n        }\n    }\n}\n\npublic class ExampleClass\n{\n    public event EventHandler<BooleanEventArgs> EventName;\n}\n\npublic class BooleanEventArgs : EventArgs\n{\n    public bool Value { get; }\n\n    public BooleanEventArgs(bool value)\n    {\n        Value = value;\n    }\n}