using System;\nusing System.Windows.Forms;\nusing WeifenLuo.WinFormsUI.Docking;\n\nnamespace DockableFormExample\n{\n    public partial class MainForm : Form\n    {\n        private DockPanel dockPanel;\n\n        public MainForm()\n        {\n            InitializeComponent();\n            InitializeDockPanel();\n        }\n\n        private void InitializeComponent()\n        {\n            this.dockPanel = new DockPanel();\n            this.SuspendLayout();\n            // \n            // dockPanel\n            // \n            this.dockPanel.Dock = DockStyle.Fill;\n            this.dockPanel.Location = new System.Drawing.Point(0, 0);\n            this.dockPanel.Name = "dockPanel";\n            this.dockPanel.Size = new System.Drawing.Size(800, 450);\n            this.dockPanel.TabIndex = 0;\n            // \n            // MainForm\n            // \n            this.AutoScaleDimensions = new System.Drawing.SizeF(6F, 13F);\n            this.AutoScaleMode = System.Windows.Forms.AutoScaleMode.Font;\n            this.ClientSize = new System.Drawing.Size(800, 450);\n            this.Controls.Add(this.dockPanel);\n            this.Name = "MainForm";\n            this.Text = "Dockable Form Example";\n            this.Load += new System.EventHandler(this.MainForm_Load);\n            this.ResumeLayout(false);\n        }\n\n        private void InitializeDockPanel()\n        {\n            dockPanel = new DockPanel();\n            dockPanel.Dock = DockStyle.Fill;\n            this.Controls.Add(dockPanel);\n        }\n\n        private void MainForm_Load(object sender, EventArgs e)\n        {\n            // Create and show dockable windows\n            var dockContent1 = new DockContent\n            {\n                Text = "Dockable Window 1"\n            };\n            dockContent1.Show(dockPanel, DockState.DockLeft);\n\n            var dockContent2 = new DockContent\n            {\n                Text = "Dockable Window 2"\n            };\n            dockContent2.Show(dockPanel, DockState.DockRight);\n        }\n    }\n}