protected override void OnInit(EventArgs e)\n{\n    base.OnInit(e);\n    // Ensure the control is recreated on every postback\n    CreateDynamicControls();\n}\n\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initial script registration\n        RegisterStartupScript();\n    }\n}\n\nprotected override void OnPreRender(EventArgs e)\n{\n    base.OnPreRender(e);\n    // Register script on every partial postback\n    RegisterStartupScript();\n}\n\nprivate void CreateDynamicControls()\n{\n    placeholder.Controls.Clear(); // Clear existing controls\n    placeholder.Controls.Add(Factory.CreateControl());\n}\n\nprivate void RegisterStartupScript()\n{\n    string script = "alert('Hello, World!');"; // Example script\n    ScriptManager.RegisterStartupScript(this, GetType(), Guid.NewGuid().ToString(), script, true);\n}