using System;\nusing System.Data.OleDb;\n\nclass Program\n{\n    static void Main()\n    {\n        string queryString = "SELECT System.ItemNameDisplay, System.ItemUrl FROM SystemIndex WHERE System.ItemNameDisplay LIKE '%searchterm%'";\n        \n        using (OleDbConnection connection = new OleDbConnection("Provider=Search.CollatorDSO;Extended Properties='Application=Windows'"))\n        {\n            OleDbCommand command = new OleDbCommand(queryString, connection);\n            connection.Open();\n            \n            using (OleDbDataReader reader = command.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    Console.WriteLine("Name: {0}, URL: {1}", reader["System.ItemNameDisplay"], reader["System.ItemUrl"]);\n                }\n            }\n        }\n    }\n}