using System;\nusing System.DirectoryServices;\nusing System.Web.UI;\n\npublic partial class ChangePassword : Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        \n    }\n\n    protected void ChangePasswordButton_Click(object sender, EventArgs e)\n    {\n        string username = UsernameTextBox.Text;\n        string oldPassword = OldPasswordTextBox.Text;\n        string newPassword = NewPasswordTextBox.Text;\n        string ldapPath = "LDAP://YourDomainController";\n\n        try\n        {\n            using (DirectoryEntry entry = new DirectoryEntry(ldapPath, username, oldPassword))\n            {\n                entry.Invoke("ChangePassword", new object[] { oldPassword, newPassword });\n                entry.CommitChanges();\n                Response.Write("Password changed successfully.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Response.Write("Error: " + ex.Message);\n        }\n    }\n}