using System;\nusing System.Security.Cryptography;\n\npublic class AesEncryption\n{\n    public static (byte[] encryptedData, byte[] key, byte[] iv) Encrypt(byte[] data)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            aes.GenerateKey();\n            aes.GenerateIV();\n\n            ICryptoTransform encryptor = aes.CreateEncryptor(aes.Key, aes.IV);\n\n            byte[] encryptedData = encryptor.TransformFinalBlock(data, 0, data.Length);\n\n            return (encryptedData, aes.Key, aes.IV);\n        }\n    }\n\n    public static byte[] Decrypt(byte[] encryptedData, byte[] key, byte[] iv)\n    {\n        using (Aes aes = Aes.Create())\n        {\n            ICryptoTransform decryptor = aes.CreateDecryptor(key, iv);\n\n            byte[] decryptedData = decryptor.TransformFinalBlock(encryptedData, 0, encryptedData.Length);\n\n            return decryptedData;\n        }\n    }\n\n    public static void Main()\n    {\n        byte[] data = System.Text.Encoding.UTF8.GetBytes("Hello, World!");\n        var (encryptedData, key, iv) = Encrypt(data);\n        byte[] decryptedData = Decrypt(encryptedData, key, iv);\n\n        Console.WriteLine("Original Data: " + System.Text.Encoding.UTF8.GetString(data));\n        Console.WriteLine("Encrypted Data: " + BitConverter.ToString(encryptedData));\n        Console.WriteLine("Decrypted Data: " + System.Text.Encoding.UTF8.GetString(decryptedData));\n    }\n}