using System;\nusing System.Timers;\nusing Oracle.DataAccess.Client;\n\npublic class OracleKeepAlive\n{\n    private static Timer keepAliveTimer;\n    private static string connectionString = "your_connection_string_here";\n\n    public static void Main()\n    {\n        keepAliveTimer = new Timer(600000); // Set to 10 minutes\n        keepAliveTimer.Elapsed += new ElapsedEventHandler(OnKeepAlive);\n        keepAliveTimer.Enabled = true;\n\n        // Your app code here...\n\n        // Prevent the application from closing immediately\n        Console.ReadLine();\n    }\n\n    private static void OnKeepAlive(object source, ElapsedEventArgs e)\n    {\n        using (OracleConnection conn = new OracleConnection(connectionString))\n        {\n            try\n            {\n                conn.Open();\n                OracleCommand cmd = new OracleCommand("SELECT 1 FROM DUAL", conn);\n                cmd.ExecuteNonQuery();\n            }\n            catch (Exception ex)\n            {\n                // Log the exception\n                Console.WriteLine("Keep-alive query failed: " + ex.Message);\n            }\n        }\n    }\n}