using System;\nusing System.Xml.Xsl;\nusing System.Configuration;\n\nnamespace UserTransform\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string oldXML = ConfigurationManager.AppSettings["XMLToBeTransformed"];\n            string xsltLocation = ConfigurationManager.AppSettings["XSLTfile"];\n            string newCSV = ConfigurationManager.AppSettings["NewCSVLocation"];\n\n            XslCompiledTransform transform = new XslCompiledTransform();\n            transform.Load(xsltLocation);\n            transform.Transform(oldXML, newCSV);\n        }\n    }\n}