using System;\nusing System.Security;\nusing System.Security.Permissions;\n\n[AttributeUsage(AttributeTargets.Method | AttributeTargets.Class, AllowMultiple = true)]\npublic class MyCustomPermissionAttribute : CodeAccessSecurityAttribute\n{\n    public MyCustomPermissionAttribute(SecurityAction action) : base(action)\n    {\n    }\n\n    public override IPermission CreatePermission()\n    {\n        return new MyCustomPermission(PermissionState.Unrestricted);\n    }\n}