using Microsoft.CodeAnalysis;\nusing Microsoft.CodeAnalysis.VisualBasic;\nusing Microsoft.CodeAnalysis.VisualBasic.Syntax;\nusing System;\nusing System.IO;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string filePath = "path/to/your/vbnetfile.vb";\n        string code = File.ReadAllText(filePath);\n\n        SyntaxTree tree = VisualBasicSyntaxTree.ParseText(code);\n        var root = tree.GetRoot();\n\n        var methods = root.DescendantNodes().OfType<MethodBlockSyntax>();\n\n        foreach (var method in methods)\n        {\n            Console.WriteLine("Method found:");\n            Console.WriteLine(method.ToFullString());\n            Console.WriteLine();\n        }\n    }\n}