using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Import the function from the 32-bit DLL\n    [DllImport("Win32DLL.dll", CallingConvention = CallingConvention.Cdecl)]\n    public static extern int SomeFunction(int param);\n\n    static void Main()\n    {\n        try\n        {\n            int result = SomeFunction(42);\n            Console.WriteLine("Result from Win32 DLL: " + result);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}