using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlString = @"<root>\n                                <item>\n                                    <title><p>some title</p></title>\n                                </item>\n                             </root>";\n                             \n        XElement root = XElement.Parse(xmlString);\n\n        var titleWithTags = root.Descendants("title")\n                                .Select(title => title.Value + title.FirstNode.ToString())\n                                .FirstOrDefault();\n                                \n        Console.WriteLine(titleWithTags); // Output: some title<p>some title</p>\n    }\n}