using NUnit.Framework;\nusing NUnit.Framework.Constraints;\n\npublic class CustomConstraint : Constraint\n{\n    private readonly string expected;\n\n    public CustomConstraint(string expected)\n    {\n        this.expected = expected;\n    }\n\n    public override bool Matches(object actual)\n    {\n        this.actual = actual;\n        return actual is string actualString && actualString == expected;\n    }\n\n    public override void WriteDescriptionTo(MessageWriter writer)\n    {\n        writer.Write($"a string equal to \"{expected}\"");\n    }\n\n    public override void WriteMessageTo(MessageWriter writer)\n    {\n        writer.DisplayStringDifferences(expected, actual as string, 0, false, false);\n    }\n\n    public override void WriteActualValueTo(MessageWriter writer)\n    {\n        writer.WriteActualValue(actual);\n    }\n}