using System;\nusing System.Data.SqlClient;\nusing System.Windows.Forms;\n\nnamespace DatabaseBackupRestore\n{\n    public partial class MainForm : Form\n    {\n        private string connectionString = "your_connection_string_here";\n\n        public MainForm()\n        {\n            InitializeComponent();\n        }\n\n        private void BackupDatabase(string backupFilePath)\n        {\n            string backupQuery = $@"\n                BACKUP DATABASE [YourDatabaseName] \n                TO DISK = @backupFilePath \n                WITH FORMAT, MEDIANAME = 'Z_SQLServerBackups', NAME = 'Full Backup of YourDatabaseName';";\n\n            ExecuteNonQuery(backupQuery, backupFilePath);\n        }\n\n        private void RestoreDatabase(string backupFilePath)\n        {\n            string restoreQuery = $@"\n                RESTORE DATABASE [YourDatabaseName] \n                FROM DISK = @backupFilePath \n                WITH FILE = 1, NOUNLOAD, REPLACE, STATS = 10;";\n\n            ExecuteNonQuery(restoreQuery, backupFilePath);\n        }\n\n        private void ExecuteNonQuery(string query, string filePath)\n        {\n            using (SqlConnection connection = new SqlConnection(connectionString))\n            {\n                SqlCommand command = new SqlCommand(query, connection);\n                command.Parameters.AddWithValue("@backupFilePath", filePath);\n\n                try\n                {\n                    connection.Open();\n                    command.ExecuteNonQuery();\n                    MessageBox.Show("Operation completed successfully.");\n                }\n                catch (Exception ex)\n                {\n                    MessageBox.Show("Error: " + ex.Message);\n                }\n            }\n        }\n\n        private void backupButton_Click(object sender, EventArgs e)\n        {\n            string backupFilePath = @"C:\path\to\your\backup.bak";\n            BackupDatabase(backupFilePath);\n        }\n\n        private void restoreButton_Click(object sender, EventArgs e)\n        {\n            string backupFilePath = @"C:\path\to\your\backup.bak";\n            RestoreDatabase(backupFilePath);\n        }\n    }\n}