using System.ComponentModel.DataAnnotations;\n\npublic class YourModel : IDataErrorInfo\n{\n    [Required(ErrorMessage = "Name is required")]\n    public string Name { get; set; }\n\n    [Range(1, 100, ErrorMessage = "Age must be between 1 and 100")]\n    public int Age { get; set; }\n\n    // Implement IDataErrorInfo\n    public string this[string columnName]\n    {\n        get\n        {\n            var context = new ValidationContext(this, null, null) { MemberName = columnName };\n            var result = new List<ValidationResult>();\n            bool isValid = Validator.TryValidateProperty(\n                GetType().GetProperty(columnName).GetValue(this, null),\n                context,\n                result\n            );\n\n            if (!isValid)\n            {\n                return result.First().ErrorMessage;\n            }\n            return null;\n        }\n    }\n\n    public string Error\n    {\n        get\n        {\n            var context = new ValidationContext(this, null, null);\n            var result = new List<ValidationResult>();\n            bool isValid = Validator.TryValidateObject(this, context, result, true);\n\n            if (!isValid)\n            {\n                return string.Join(Environment.NewLine, result.Select(r => r.ErrorMessage));\n            }\n            return null;\n        }\n    }\n}