using System;\nusing System.Web;\nusing System.IO;\n\npublic class Global : HttpApplication\n{\n    protected void Application_BeginRequest(Object sender, EventArgs e)\n    {\n        string userIdentity = HttpContext.Current.User?.Identity?.Name ?? "Anonymous";\n        string logMessage = $"Request from: {userIdentity} at {DateTime.Now}\n";\n        File.AppendAllText(HttpContext.Current.Server.MapPath("~/App_Data/AccessLog.txt"), logMessage);\n    }\n}