using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        Uri baseAddress = new Uri("net.pipe://localhost/MyService");\n        ChannelFactory<IMyService> factory = new ChannelFactory<IMyService>(new NetNamedPipeBinding(), new EndpointAddress(baseAddress));\n\n        IMyService proxy = factory.CreateChannel();\n        proxy.SendMessage("Hello from the second instance!");\n\n        ((IChannel)proxy).Close();\n        factory.Close();\n    }\n}