using System;\nusing System.Net;\nusing System.Net.Security;\nusing System.Security.Cryptography.X509Certificates;\n\npublic class Program\n{\n    private static string knownFingerprint = "YOUR_KNOWN_CERTIFICATE_FINGERPRINT"; // Replace with your known fingerprint\n\n    public static void Main()\n    {\n        ServicePointManager.ServerCertificateValidationCallback += ValidateServerCertificate;\n        // Make your HTTPS request here, e.g., using HttpClient or WebRequest\n    }\n\n    private static bool ValidateServerCertificate(object sender, X509Certificate certificate, X509Chain chain, SslPolicyErrors sslPolicyErrors)\n    {\n        if (sslPolicyErrors == SslPolicyErrors.None)\n        {\n            string certFingerprint = GetCertificateFingerprint(certificate);\n            return certFingerprint == knownFingerprint;\n        }\n        return false;\n    }\n\n    private static string GetCertificateFingerprint(X509Certificate certificate)\n    {\n        byte[] certHash = certificate.GetCertHash();\n        return BitConverter.ToString(certHash).Replace("-", ":");\n    }\n}