using Microsoft.Win32;\nusing System;\n\npublic class ComChecker\n{\n    public static bool IsComObjectRegistered(string progId)\n    {\n        try\n        {\n            // Open the CLSID key for the given ProgID\n            string clsid = (string)Registry.ClassesRoot.OpenSubKey(progId + "\\CLSID")?.GetValue(null);\n            if (clsid != null)\n            {\n                // Check if the CLSID key exists in the registry\n                using (RegistryKey key = Registry.ClassesRoot.OpenSubKey("CLSID\\" + clsid))\n                {\n                    return key != null;\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error checking COM registration: " + ex.Message);\n        }\n        \n        return false;\n    }\n\n    public static void Main()\n    {\n        string progId = "Foo.Bar"; // Replace with your COM ProgID\n        bool isRegistered = IsComObjectRegistered(progId);\n        Console.WriteLine($"Is {progId} registered? {isRegistered}");\n    }\n}