using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Import the SendMessage function from user32.dll\n    [DllImport("user32.dll")]\n    private static extern int SendMessage(IntPtr hWnd, int Msg, IntPtr wParam, IntPtr lParam);\n\n    // Constants for the SendMessage function\n    private const int WM_CLOSE = 0x0010;\n\n    static void Main()\n    {\n        // Find the process by name\n        foreach (Process proc in Process.GetProcessesByName("myapp"))\n        {\n            // Check if the process has a main window handle\n            if (proc.MainWindowHandle != IntPtr.Zero)\n            {\n                // Send WM_CLOSE message to the main window\n                SendMessage(proc.MainWindowHandle, WM_CLOSE, IntPtr.Zero, IntPtr.Zero);\n            }\n        }\n    }\n}