using System.Data;\nusing FirebirdSql.Data.FirebirdClient;\n\nstring tableName = "yourTableName";\nstring columnName = "yourColumnName";\nstring searchValue = "JOHN";\n\nstring sql = $"SELECT * FROM {tableName} WHERE {columnName} LIKE @searchValue";\nusing (FbConnection connection = new FbConnection("your_connection_string"))\n{\n    using (FbCommand cmd = new FbCommand(sql, connection))\n    {\n        cmd.Parameters.AddWithValue("@searchValue", "%" + searchValue + "%");\n\n        connection.Open();\n        using (FbDataReader reader = cmd.ExecuteReader())\n        {\n            while (reader.Read())\n            {\n                // Process the results\n            }\n        }\n    }\n}