using System;\nusing System.IO;\nusing System.Linq;\nusing GlobExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string directoryPath = @"C:\your\directory\path";\n\n        // Example pattern: Matches all files in SVN directories that are located \n        // anywhere in the directory tree under dir1/dir2\n        var pattern1 = "dir1/dir2/**/SVN/*";\n\n        // Example pattern: Matches all files that have a test element in their path,\n        // including test as a filename.\n        var pattern2 = "**/test/**";\n\n        var files1 = Glob.Files(directoryPath, pattern1).ToList();\n        var files2 = Glob.Files(directoryPath, pattern2).ToList();\n\n        Console.WriteLine("Files matching pattern 1:");\n        files1.ForEach(Console.WriteLine);\n\n        Console.WriteLine("\nFiles matching pattern 2:");\n        files2.ForEach(Console.WriteLine);\n    }\n}