using System;\nusing System.Windows.Forms;\nusing System.Reflection;\n\nnamespace OfficeInteropExample\n{\n    public partial class Form1 : Form\n    {\n        public Form1()\n        {\n            InitializeComponent();\n        }\n\n        private void btnOpenExcel_Click(object sender, EventArgs e)\n        {\n            OpenExcel();\n        }\n\n        private void OpenExcel()\n        {\n            object excelApp = null;\n            try\n            {\n                // Create a new instance of the Excel application\n                excelApp = Activator.CreateInstance(Type.GetTypeFromProgID("Excel.Application"));\n                if (excelApp == null)\n                {\n                    MessageBox.Show("Excel is not installed on this system.");\n                    return;\n                }\n\n                // Make Excel visible\n                excelApp.GetType().InvokeMember("Visible", BindingFlags.SetProperty, null, excelApp, new object[] { true });\n\n                // Add a new workbook\n                object workbooks = excelApp.GetType().InvokeMember("Workbooks", BindingFlags.GetProperty, null, excelApp, null);\n                workbooks.GetType().InvokeMember("Add", BindingFlags.InvokeMethod, null, workbooks, null);\n\n                MessageBox.Show("Excel opened successfully.");\n            }\n            catch (Exception ex)\n            {\n                MessageBox.Show($"An error occurred: {ex.Message}");\n            }\n            finally\n            {\n                if (excelApp != null)\n                {\n                    // Release the Excel application object\n                    Marshal.ReleaseComObject(excelApp);\n                }\n            }\n        }\n    }\n}