using System;\nusing System.Windows.Forms;\n\npublic class MasterForm : Form\n{\n    private DataGridView masterGridView;\n    private BindingSource masterBindingSource;\n\n    public MasterForm()\n    {\n        masterBindingSource = new BindingSource();\n\n        // Dummy data source\n        var dataSource = new BindingList<MasterData>\n        {\n            new MasterData { Id = 1, Name = "Master 1" },\n            new MasterData { Id = 2, Name = "Master 2" }\n        };\n        masterBindingSource.DataSource = dataSource;\n\n        masterGridView = new DataGridView\n        {\n            Dock = DockStyle.Fill,\n            DataSource = masterBindingSource,\n            AutoGenerateColumns = true\n        };\n\n        masterGridView.CellDoubleClick += MasterGridView_CellDoubleClick;\n        Controls.Add(masterGridView);\n    }\n\n    private void MasterGridView_CellDoubleClick(object sender, DataGridViewCellEventArgs e)\n    {\n        if (e.RowIndex >= 0)\n        {\n            var selectedMaster = (MasterData)masterBindingSource[e.RowIndex];\n            DetailForm detailForm = new DetailForm(selectedMaster);\n            detailForm.Show();\n        }\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new MasterForm());\n    }\n}\n\npublic class MasterData\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}