public class FinancialDataRepository\n     {\n         private readonly string connectionString;\n\n         public FinancialDataRepository(string connectionString)\n         {\n             this.connectionString = connectionString;\n         }\n\n         public IEnumerable<FinancialData> GetAllFinancialData()\n         {\n             var dataList = new List<FinancialData>();\n\n             using (SqlConnection connection = new SqlConnection(connectionString))\n             {\n                 connection.Open();\n                 SqlCommand command = new SqlCommand("SELECT * FROM FinancialData", connection);\n                 SqlDataReader reader = command.ExecuteReader();\n\n                 while (reader.Read())\n                 {\n                     var data = new FinancialData\n                     {\n                         Id = (int)reader["Id"],\n                         Description = reader["Description"].ToString(),\n                         Amount = (decimal)reader["Amount"],\n                         Date = (DateTime)reader["Date"]\n                     };\n                     dataList.Add(data);\n                 }\n             }\n\n             return dataList;\n         }\n\n         // Add methods for Insert, Update, Delete as needed\n     }