using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\n\nclass Program\n{\n    static void Main()\n    {\n        string imagePath = "path_to_your_image.jpg";\n\n        using (Image img = Image.FromFile(imagePath))\n        {\n            PropertyItem[] propItems = img.PropertyItems;\n\n            foreach (PropertyItem propItem in propItems)\n            {\n                switch (propItem.Id)\n                {\n                    case 0x9003: // Date Picture Taken\n                        string dateTaken = System.Text.Encoding.ASCII.GetString(propItem.Value);\n                        Console.WriteLine("Date Picture Taken: " + dateTaken);\n                        break;\n                    case 0x9201: // Shutter Speed\n                        string shutterSpeed = BitConverter.ToUInt32(propItem.Value, 0).ToString();\n                        Console.WriteLine("Shutter Speed: " + shutterSpeed);\n                        break;\n                    case 0x0110: // Camera Model\n                        string cameraModel = System.Text.Encoding.ASCII.GetString(propItem.Value);\n                        Console.WriteLine("Camera Model: " + cameraModel);\n                        break;\n                }\n            }\n        }\n    }\n}