using System.Windows;\nusing System.Windows.Documents;\nusing System.Windows.Media;\n\nnamespace WpfApp\n{\n    public partial class MainWindow : Window\n    {\n        private const int MaxLength = 10; // Set your maximum length here\n\n        public MainWindow()\n        {\n            InitializeComponent();\n        }\n\n        private void RichTextBox_TextChanged(object sender, System.Windows.Controls.TextChangedEventArgs e)\n        {\n            var richTextBox = sender as RichTextBox;\n            var textRange = new TextRange(richTextBox.Document.ContentStart, richTextBox.Document.ContentEnd);\n            string text = textRange.Text.TrimEnd('\r', '\n');\n\n            richTextBox.Document.Blocks.Clear();\n            Paragraph paragraph = new Paragraph();\n\n            if (text.Length <= MaxLength)\n            {\n                paragraph.Inlines.Add(new Run(text));\n            }\n            else\n            {\n                paragraph.Inlines.Add(new Run(text.Substring(0, MaxLength)));\n                paragraph.Inlines.Add(new Run(text.Substring(MaxLength)) { Foreground = Brushes.Red });\n            }\n\n            richTextBox.Document.Blocks.Add(paragraph);\n        }\n    }\n}