using System;\nusing System.Windows.Forms;\n\npublic class DetailForm : Form\n{\n    private Label idLabel;\n    private Label nameLabel;\n    private BindingSource detailBindingSource;\n\n    public DetailForm(MasterData selectedMaster)\n    {\n        detailBindingSource = new BindingSource();\n        detailBindingSource.DataSource = selectedMaster;\n\n        idLabel = new Label\n        {\n            Location = new System.Drawing.Point(20, 20),\n            AutoSize = true\n        };\n        idLabel.DataBindings.Add("Text", detailBindingSource, "Id");\n\n        nameLabel = new Label\n        {\n            Location = new System.Drawing.Point(20, 50),\n            AutoSize = true\n        };\n        nameLabel.DataBindings.Add("Text", detailBindingSource, "Name");\n\n        Controls.Add(idLabel);\n        Controls.Add(nameLabel);\n    }\n}